const fruit = ["Manzana", "Banano"];
console.log(fruit.length);
console.log(fruit[0]);

//Añadir un elemento al inicio
fruit.unshift("uva");

//Añadir un elemento al final
fruit.push("Melocoton");

//Eliminar el ultimo elemento
fruit.pop();

//Eliminar el primer elemento
fruit.shift();

//Averiguial el indice de un elemento
fruit.indexOf("Banano");
